DECLARE @cols AS NVARCHAR(MAX),
        @query  AS NVARCHAR(MAX);

-- Create a list of columns for all months for the PIVOT statement
SET @cols = STUFF((SELECT DISTINCT ',' + QUOTENAME(Codigo) 
                   FROM dbo.truckscript
                   FOR XML PATH(''), TYPE
                   ).value('.', 'NVARCHAR(MAX)') 
               ,1,1,'');

-- Build the dynamic query for all months
SET @query = 'SELECT Division, Sociedad, Pais, [Planta / Almacén], Grupo, ' + @cols + '
              FROM 
              (
                  SELECT Division, Sociedad, Pais, [Planta / Almacén], Grupo, Codigo, 
                         Mes, Valor
                  FROM dbo.truckscript
              ) AS SourceTable
              PIVOT 
              (
                  MAX(Valor)
                  FOR Mes IN ([Enero], [Febrero], [Marzo], [Abril], [Mayo], [Junio], 
                              [Julio], [Agosto], [Septiembre], [Octubre], [Noviembre], [Diciembre])
              ) AS PivotTable';

-- Execute the dynamic SQL
EXEC sp_executesql @query;
