DECLARE @cols AS NVARCHAR(MAX),
        @query AS NVARCHAR(MAX),
        @month AS NVARCHAR(50),
        @monthList AS NVARCHAR(MAX);

-- Define the months to pivot
SET @monthList = 'Enero, Febrero, Marzo, Abril, Mayo, Junio, Julio, Agosto, Septiembre, Octubre, Noviembre, Dicembre';

-- Generate the distinct Codigo list for pivot columns
SET @cols = STUFF((SELECT DISTINCT ',' + QUOTENAME(Codigo)
                   FROM dbo.truckscript
                   FOR XML PATH(''), TYPE
                   ).value('.', 'NVARCHAR(MAX)'), 1, 1, '');

-- Initialize the main query string
SET @query = '';

-- Loop through each month and create a separate pivot table for each
WHILE LEN(@monthList) > 0
BEGIN
    -- Extract each month name from @monthList
    SET @month = LEFT(@monthList, CHARINDEX(',', @monthList + ',') - 1);
    SET @monthList = STUFF(@monthList, 1, LEN(@month) + 1, '');

    -- Build the pivot query for the current month
    SET @query = @query + '
        SELECT Division, Sociedad, Pais, [Planta / Almacén], Grupo, Descripción, ' + QUOTENAME(@month) + ' AS Month, ' + @cols + '
        FROM (
            SELECT Division, Sociedad, Pais, [Planta / Almacén], Grupo, Descripción, Codigo, ' + @month + ' AS MonthValue
            FROM dbo.truckscript
        ) AS SourceTable
        PIVOT (
            MAX(MonthValue) FOR Codigo IN (' + @cols + ')
        ) AS PivotTable_' + @month + '
        UNION ALL ';
END

-- Remove the last UNION ALL
SET @query = LEFT(@query, LEN(@query) - 10);

-- Execute the dynamic SQL query
EXEC sp_executesql @query;
